--Demesio: siuos skriptus praleisti IGNITIS useryje

create table klasifik
(
  klasifik_id number primary key,
  pavadinimas varchar2(100 char),
  tipas number
);

create table reg_poz
(
  --Ant naujesnes Oracle versijos rasyciau: reg_pozymiai_id NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY
  reg_poz_id number primary key,
  pavadinimas varchar2(100 char),
  tipas number
);


create table reg_poz_klasifik
(
  reg_poz_klasifik_id number primary key,
  reg_poz_id number,
  klasifik_id number,

  constraint fk_reg_poz_klasifik_reg_poz foreign key(reg_poz_id) references reg_poz(reg_poz_id),
  constraint fk_reg_poz_klasifik_klasifik foreign key(klasifik_id) references klasifik(klasifik_id)
);

